    @Test
    public void testGetWfMap() throws Exception {
        VehicleAnalyzer analyzer = new VehicleAnalyzer();

        analyzer.addStatData(JsonUtils.toVehicle(json1));
        analyzer.addStatData(JsonUtils.toVehicle(json2));
        analyzer.addStatData(JsonUtils.toVehicle(json3));
        analyzer.addStatData(JsonUtils.toVehicle(json4));
        analyzer.addStatData(JsonUtils.toVehicle(json5));

        Vehicle vehicle1 = JsonUtils.toVehicle(targetVehicleJson1);
        Vehicle vehicle2 = JsonUtils.toVehicle(targetVehicleJson2);
        Vehicle vehicle3 = JsonUtils.toVehicle(targetVehicleJson3);
        Vehicle vehicle4 = JsonUtils.toVehicle(targetVehicleJson4);

        Map<VehicleParam, Float> wfMap1 = analyzer.getWfMap(vehicle1);
        Map<VehicleParam, Float> wfMap2 = analyzer.getWfMap(vehicle2);
        Map<VehicleParam, Float> wfMap3 = analyzer.getWfMap(vehicle3);
        Map<VehicleParam, Float> wfMap4 = analyzer.getWfMap(vehicle4);
        assertNotNull(wfMap1);
        assertNotNull(wfMap2);
        assertNotNull(wfMap3);
        assertNotNull(wfMap4);

        float wf1 = analyzer.getWf(vehicle1);
        float wf2 = analyzer.getWf(vehicle2);
        float wf3 = analyzer.getWf(vehicle3);
        float wf4 = analyzer.getWf(vehicle4);
        assertTrue(wf1 > 0);
        assertTrue(wf2 > 0);
        assertTrue(wf3 > 0);
        assertTrue(wf4 > 0);

        SummaryStatistics statistics = new SummaryStatistics();
        statistics.addValue(wf1);
        statistics.addValue(wf2);
        statistics.addValue(wf3);
        statistics.addValue(wf4);

        assertTrue(wf2 == statistics.getMax());
    }
